        if (r.orderType == OrderType.MARKET_OPEN) {
            c.openTradeMarketCallback(a, o);
        } else {
            c.closeTradeMarketCallback(a, o);
        }
        emit CallbackExecuted(a, r.orderType);

        emit PriceReceived(
