        }
        emit CallbackExecuted(a, r.orderType);

        emit PriceReceived(
            "0x",
            orderId,
            msg.sender,
            r.pairIndex,
            0,
            0,
            r.linkFeePerNode,
            r.isLookback,
            usedInMedian
        );
    }

