    // Calculate LINK fee for each request
    function linkFee(
        uint pairIndex,
        uint leveragedPosWETH
    ) public view returns (uint) {
        (, int linkPriceUsd, , , ) = linkPriceFeed.latestRoundData();

        return
            (pairsStorage.pairOracleFeeP(pairIndex) * leveragedPosWETH * 1e8) /
            uint(linkPriceUsd) /
            PRECISION /
            100;
    }

    // Claim back LINK tokens (if contract will be replaced for example)
    function claimBackLink() external onlyGov {
        TokenInterface link = storageT.linkErc677();

        link.transfer(storageT.gov(), link.balanceOf(address(this)));
    }

    // Utils
    function fetchFeedPrice(
        PairsStorageInterfaceV6.Feed memory f
    ) private view returns (uint) {
        if (f.feed1 == address(0)) {
            return 0;
        }

        uint feedPrice;
        (, int feedPrice1, , , ) = ChainlinkFeedInterface(f.feed1)
            .latestRoundData();

        if (
            f.feedCalculation == PairsStorageInterfaceV6.FeedCalculation.DEFAULT
        ) {
            feedPrice = uint((feedPrice1 * int(PRECISION)) / 1e8);
        } else if (
            f.feedCalculation == PairsStorageInterfaceV6.FeedCalculation.INVERT
        ) {
            feedPrice = uint((int(PRECISION) * 1e8) / feedPrice1);
        } else {
            (, int feedPrice2, , , ) = ChainlinkFeedInterface(f.feed2)
                .latestRoundData();
            feedPrice = uint((feedPrice1 * int(PRECISION)) / feedPrice2);
        }

        return feedPrice;
    }

    function isPriceWithinDeviation(
        uint price,
        uint feedPrice,
        uint maxDeviationP
    ) private pure returns (bool) {
        return
            price == 0 ||
            feedPrice == 0 ||
            ((price >= feedPrice ? price - feedPrice : feedPrice - price) *
                PRECISION *
                100) /
                feedPrice <=
            maxDeviationP;
    }

    // Median function
    function swap(uint[] memory array, uint i, uint j) private pure {
        (array[i], array[j]) = (array[j], array[i]);
    }

    function sort(uint[] memory array, uint begin, uint end) private pure {
        if (begin >= end) {
            return;
        }

        uint j = begin;
        uint pivot = array[j];

        for (uint i = begin + 1; i < end; ++i) {
            if (array[i] < pivot) {
                swap(array, i, ++j);
            }
        }

        swap(array, begin, j);
        sort(array, begin, j);
        sort(array, j + 1, end);
    }

    function median(uint[] memory array) private pure returns (uint) {
        sort(array, 0, array.length);

        return
            array.length % 2 == 0
                ? (array[array.length / 2 - 1] + array[array.length / 2]) / 2
                : array[array.length / 2];
    }

    function medianLookbacks(
        LookbackOrderAnswer[] memory array
    ) private pure returns (uint open, uint high, uint low) {
        uint length = array.length;

        uint[] memory opens = new uint[](length);
        uint[] memory highs = new uint[](length);
        uint[] memory lows = new uint[](length);

        for (uint i; i < length; ) {
            opens[i] = array[i].open;
            highs[i] = array[i].high;
            lows[i] = array[i].low;

            unchecked {
                ++i;
            }
        }

        sort(opens, 0, length);
        sort(highs, 0, length);
        sort(lows, 0, length);

        bool isLengthEven = length % 2 == 0;
        uint halfLength = length / 2;

        open = isLengthEven
            ? (opens[halfLength - 1] + opens[halfLength]) / 2
            : opens[halfLength];
        high = isLengthEven
            ? (highs[halfLength - 1] + highs[halfLength]) / 2
            : highs[halfLength];
        low = isLengthEven
            ? (lows[halfLength - 1] + lows[halfLength]) / 2
            : lows[halfLength];
    }

    // Storage v5 compatibility
    function openFeeP(uint pairIndex) external view returns (uint) {
        return pairsStorage.pairOpenFeeP(pairIndex);
    }
}

contracts/Referrals.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "./interfaces/UniswapRouterInterface.sol";
import "./interfaces/TokenInterface.sol";
import "./interfaces/NftInterfaceV5.sol";
import "./interfaces/VaultInterface.sol";
import "./interfaces/PairsStorageInterfaceV6.sol";
import "./interfaces/StorageInterface.sol";
import "./interfaces/AggregatorInterfaceV1_2.sol";
import "./interfaces/NftRewardsInterfaceV6.sol";

contract Referrals {
    // CONSTANTS
    uint constant PRECISION = 1e10;
    StorageInterface public immutable storageT;

    // ADJUSTABLE PARAMETERS
    uint public allyFeeP; // % (of referrer fees going to allies, eg. 10)
    uint public startReferrerFeeP; // % (of referrer fee when 0 volume referred, eg. 75)
    uint public openFeeP; // % (of opening fee used for referral system, eg. 33)
    uint public targetVolumeWETH; // WETH (to reach maximum referral system fee, eg. 1e8)

    // CUSTOM TYPES
    struct AllyDetails {
        address[] referrersReferred;
        uint volumeReferredWETH; // 1e18
        uint pendingRewardsToken; // 1e18
        uint totalRewardsToken; // 1e18
        uint totalRewardsValueWETH; // 1e18
        bool active;
    }

    struct ReferrerDetails {
        address ally;
        address[] tradersReferred;
        uint volumeReferredWETH; // 1e18
        uint pendingRewardsToken; // 1e18
        uint totalRewardsToken; // 1e18
        uint totalRewardsValueWETH; // 1e18
        bool active;
    }

    // STATE (MAPPINGS)
    mapping(address => AllyDetails) public allyDetails;
    mapping(address => ReferrerDetails) public referrerDetails;

    mapping(address => address) public referrerByTrader;

    // EVENTS
    event UpdatedAllyFeeP(uint value);
    event UpdatedStartReferrerFeeP(uint value);
    event UpdatedOpenFeeP(uint value);
    event UpdatedTargetVolumeWETH(uint value);

    event AllyWhitelisted(address indexed ally);
    event AllyUnwhitelisted(address indexed ally);

    event ReferrerWhitelisted(address indexed referrer, address indexed ally);
    event ReferrerUnwhitelisted(address indexed referrer);
    event ReferrerRegistered(address indexed trader, address indexed referrer);

    event AllyRewardDistributed(
        address indexed ally,
        address indexed trader,
        uint volumeWETH,
        uint amountToken,
        uint amountValueWETH
    );
    event ReferrerRewardDistributed(
        address indexed referrer,
        address indexed trader,
        uint volumeWETH,
        uint amountToken,
        uint amountValueWETH
    );

    event AllyRewardsClaimed(address indexed ally, uint amountToken);
    event ReferrerRewardsClaimed(address indexed referrer, uint amountToken);

    constructor(
        StorageInterface _storageT,
        uint _allyFeeP,
        uint _startReferrerFeeP,
        uint _openFeeP,
        uint _targetVolumeWETH
    ) {
        require(
            address(_storageT) != address(0) &&
                _allyFeeP <= 50 &&
                _startReferrerFeeP <= 100 &&
                _openFeeP <= 50 &&
                _targetVolumeWETH > 0,
            "WRONG_PARAMS"
        );

        storageT = _storageT;

        allyFeeP = _allyFeeP;
        startReferrerFeeP = _startReferrerFeeP;
        openFeeP = _openFeeP;
        targetVolumeWETH = _targetVolumeWETH;
    }

    // MODIFIERS
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyTrading() {
        require(msg.sender == address(storageT.trading()), "TRADING_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == address(storageT.callbacks()), "CALLBACKS_ONLY");
        _;
    }

    // MANAGE PARAMETERS
    function updateAllyFeeP(uint value) external onlyGov {
        require(value <= 50, "VALUE_ABOVE_50");

        allyFeeP = value;

        emit UpdatedAllyFeeP(value);
    }

    function updateStartReferrerFeeP(uint value) external onlyGov {
        require(value <= 100, "VALUE_ABOVE_100");

        startReferrerFeeP = value;

        emit UpdatedStartReferrerFeeP(value);
    }

    function updateOpenFeeP(uint value) external onlyGov {
        require(value <= 50, "VALUE_ABOVE_50");

        openFeeP = value;

        emit UpdatedOpenFeeP(value);
    }

    function updateTargetVolumeWETH(uint value) external onlyGov {
        require(value > 0, "VALUE_0");

        targetVolumeWETH = value;

        emit UpdatedTargetVolumeWETH(value);
    }

    // MANAGE ALLIES
    function whitelistAlly(address ally) external onlyGov {
        require(ally != address(0), "ADDRESS_0");

        AllyDetails storage a = allyDetails[ally];
        require(!a.active, "ALLY_ALREADY_ACTIVE");

        a.active = true;

        emit AllyWhitelisted(ally);
    }

    function unwhitelistAlly(address ally) external onlyGov {
        AllyDetails storage a = allyDetails[ally];
        require(a.active, "ALREADY_UNACTIVE");

        a.active = false;

        emit AllyUnwhitelisted(ally);
    }

    // MANAGE REFERRERS
    function whitelistReferrer(
        address referrer,
        address ally
    ) external onlyGov {
        require(referrer != address(0), "ADDRESS_0");

        ReferrerDetails storage r = referrerDetails[referrer];
        require(!r.active, "REFERRER_ALREADY_ACTIVE");

        r.active = true;

        if (ally != address(0)) {
            AllyDetails storage a = allyDetails[ally];
            require(a.active, "ALLY_NOT_ACTIVE");

            r.ally = ally;
            a.referrersReferred.push(referrer);
        }

        emit ReferrerWhitelisted(referrer, ally);
    }

    function unwhitelistReferrer(address referrer) external onlyGov {
        ReferrerDetails storage r = referrerDetails[referrer];
        require(r.active, "ALREADY_UNACTIVE");

        r.active = false;

        emit ReferrerUnwhitelisted(referrer);
    }

    function registerPotentialReferrer(
        address trader,
        address referrer
    ) external onlyTrading {
        ReferrerDetails storage r = referrerDetails[referrer];

        if (
            referrerByTrader[trader] != address(0) ||
            referrer == address(0) ||
            !r.active
        ) {
            return;
        }

        referrerByTrader[trader] = referrer;
        r.tradersReferred.push(trader);

        emit ReferrerRegistered(trader, referrer);
    }

    // REWARDS DISTRIBUTION
    function distributePotentialReward(
        address trader,
        uint volumeWETH,
        uint pairOpenFeeP,
        uint tokenPriceWETH
    ) external onlyCallbacks returns (uint) {
        address referrer = referrerByTrader[trader];
        ReferrerDetails storage r = referrerDetails[referrer];

        if (!r.active) {
            return 0;
        }

        uint referrerRewardValueWETH = (volumeWETH *
            getReferrerFeeP(pairOpenFeeP, r.volumeReferredWETH)) /
            PRECISION /
            100;

        uint referrerRewardToken = (referrerRewardValueWETH * PRECISION) /
            tokenPriceWETH;

        storageT.handleTokens(address(this), referrerRewardToken, true);

        AllyDetails storage a = allyDetails[r.ally];

        uint allyRewardValueWETH;
        uint allyRewardToken;

        if (a.active) {
            allyRewardValueWETH = (referrerRewardValueWETH * allyFeeP) / 100;
            allyRewardToken = (referrerRewardToken * allyFeeP) / 100;

            a.volumeReferredWETH += volumeWETH;
            a.pendingRewardsToken += allyRewardToken;
            a.totalRewardsToken += allyRewardToken;
            a.totalRewardsValueWETH += allyRewardValueWETH;

            referrerRewardValueWETH -= allyRewardValueWETH;
            referrerRewardToken -= allyRewardToken;

            emit AllyRewardDistributed(
                r.ally,
                trader,
                volumeWETH,
                allyRewardToken,
                allyRewardValueWETH
            );
        }

        r.volumeReferredWETH += volumeWETH;
        r.pendingRewardsToken += referrerRewardToken;
        r.totalRewardsToken += referrerRewardToken;
        r.totalRewardsValueWETH += referrerRewardValueWETH;

        emit ReferrerRewardDistributed(
            referrer,
            trader,
            volumeWETH,
            referrerRewardToken,
            referrerRewardValueWETH
        );

        return referrerRewardValueWETH + allyRewardValueWETH;
    }

    // REWARDS CLAIMING
    function claimAllyRewards() external {
        AllyDetails storage a = allyDetails[msg.sender];
        uint rewardsToken = a.pendingRewardsToken;

        require(rewardsToken > 0, "NO_PENDING_REWARDS");

        a.pendingRewardsToken = 0;
        storageT.token().transfer(msg.sender, rewardsToken);

        emit AllyRewardsClaimed(msg.sender, rewardsToken);
    }

    function claimReferrerRewards() external {
        ReferrerDetails storage r = referrerDetails[msg.sender];
        uint rewardsToken = r.pendingRewardsToken;

        require(rewardsToken > 0, "NO_PENDING_REWARDS");

        r.pendingRewardsToken = 0;
        storageT.token().transfer(msg.sender, rewardsToken);

        emit ReferrerRewardsClaimed(msg.sender, rewardsToken);
    }

    // VIEW FUNCTIONS
    function getReferrerFeeP(
        uint pairOpenFeeP,
        uint volumeReferredWETH
    ) public view returns (uint) {
        uint maxReferrerFeeP = (pairOpenFeeP * 2 * openFeeP) / 100;
        uint minFeeP = (maxReferrerFeeP * startReferrerFeeP) / 100;

        uint feeP = minFeeP +
            ((maxReferrerFeeP - minFeeP) * volumeReferredWETH) /
            1e18 /
            targetVolumeWETH;

        return feeP > maxReferrerFeeP ? maxReferrerFeeP : feeP;
    }

    function getPercentOfOpenFeeP(address trader) external view returns (uint) {
        return
            getPercentOfOpenFeeP_calc(
                referrerDetails[referrerByTrader[trader]].volumeReferredWETH
            );
    }

    function getPercentOfOpenFeeP_calc(
        uint volumeReferredWETH
    ) public view returns (uint resultP) {
        resultP =
            (openFeeP *
                (startReferrerFeeP *
                    PRECISION +
                    (volumeReferredWETH *
                        PRECISION *
                        (100 - startReferrerFeeP)) /
                    1e18 /
                    targetVolumeWETH)) /
            100;

        resultP = resultP > openFeeP * PRECISION
            ? openFeeP * PRECISION
            : resultP;
    }

    function getTraderReferrer(address trader) external view returns (address) {
        address referrer = referrerByTrader[trader];

        return referrerDetails[referrer].active ? referrer : address(0);
    }

    function getReferrersReferred(
        address ally
    ) external view returns (address[] memory) {
        return allyDetails[ally].referrersReferred;
    }

    function getTradersReferred(
        address referred
    ) external view returns (address[] memory) {
        return referrerDetails[referred].tradersReferred;
    }
}

contracts/Rewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/NFTRewardInterfaceV6_3.sol";
import "./interfaces/AggregatorInterfaceV1_3.sol";
import "./interfaces/IStateCopyUtils.sol";

contract Rewards is Initializable {
    // Contracts (constant)
    StorageInterface public storageT;

    // Params (constant)
    uint constant ROUND_LENGTH = 50;
    uint constant MIN_TRIGGER_TIMEOUT = 1;
    uint constant MIN_SAME_BLOCK_LIMIT = 5;
    uint constant MAX_SAME_BLOCK_LIMIT = 50;
    uint constant PRECISION = 1e10; // 10 decimals

    // Params (adjustable)
    uint public triggerTimeout; // blocks
    uint public sameBlockLimit; // bots

    // Custom data types
    struct TriggeredLimit {
        address first;
        address[] sameBlock;
        uint block;
        uint240 linkFee;
        uint16 sameBlockLimit;
    }
    struct TriggeredLimitId {
        address trader;
        uint pairIndex;
        uint index;
        StorageInterface.LimitOrder order;
    }
    struct RoundDetails {
        uint240 tokens;
        uint16 totalEntries;
    }

    enum OpenLimitOrderType {
        LEGACY,
        REVERSAL,
        MOMENTUM
    }

    // State
    uint public currentOrder; // current order in round
    uint public currentRound; // current round (1 round = 50 orders)

    mapping(uint => RoundDetails) public roundTokens; // total token rewards and entries for a round
    mapping(address => mapping(uint => uint)) public roundOrdersToClaim; // orders to claim from a round (out of 50)

    mapping(address => uint) public tokensToClaim; // rewards other than pool (first & same block)

    mapping(address => mapping(uint => mapping(uint => mapping(StorageInterface.LimitOrder => TriggeredLimit))))
        public triggeredLimits; // limits being triggered

    mapping(address => mapping(uint => mapping(uint => OpenLimitOrderType)))
        public openLimitOrderTypes;
    bool public stateCopied;

    // Tracker to prevent multiple triggers from same address or same nft
    mapping(bytes32 => bool) public botInUse;

    // Statistics
    mapping(address => uint) public tokensClaimed; // 1e18
    uint public tokensClaimedTotal; // 1e18

    // Events
    event NumberUpdated(string name, uint value);

    event TriggeredFirst(TriggeredLimitId id, address bot, uint linkFee);
    event TriggeredSameBlock(
        TriggeredLimitId id,
        address bot,
        uint linkContribution
    );
    event TriggerUnregistered(TriggeredLimitId id);
    event TriggerRewarded(
        TriggeredLimitId id,
        address first,
        uint sameBlockCount,
        uint sameBlockLimit,
        uint reward
    );

    event PoolTokensClaimed(
        address bot,
        uint fromRound,
        uint toRound,
        uint tokens
    );
    event TokensClaimed(address bot, uint tokens);

    function initialize(
        StorageInterface _storageT,
        uint _triggerTimeout,
        uint _sameBlockLimit
    ) external initializer {
        require(
            address(_storageT) != address(0) &&
                _triggerTimeout >= MIN_TRIGGER_TIMEOUT &&
                _sameBlockLimit >= MIN_SAME_BLOCK_LIMIT &&
                _sameBlockLimit <= MAX_SAME_BLOCK_LIMIT,
            "WRONG_PARAMS"
        );

        storageT = _storageT;

        triggerTimeout = _triggerTimeout;
        sameBlockLimit = _sameBlockLimit;

        currentOrder = 1;
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyTrading() {
        require(msg.sender == address(storageT.trading()), "TRADING_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == address(storageT.callbacks()), "CALLBACKS_ONLY");
        _;
    }

    function copyOldLimitTypes(uint start, uint end) external onlyGov {
        require(!stateCopied, "COPY_DONE");
        require(start <= end, "START_AFTER_END");

        NftRewardsInterfaceV6_3_1 old;

        if (block.chainid == 137) {
            // Polygon Mainnet
            old = NftRewardsInterfaceV6_3_1(
                0x3470756E5B490a974Bc25FeEeEb24c11102f5268
            );
        } else if (block.chainid == 80001) {
            // Mumbai
            old = NftRewardsInterfaceV6_3_1(
                0x3982E3de77DAd60373C0c2c539fCb93Bd288D2f5
            );
        } else if (block.chainid == 42161) {
            // Arbitrum
            old = NftRewardsInterfaceV6_3_1(
                0xc2d107e870927E3fb1127E6c1a33De5C863505b8
            );
        } else {
            revert("UNKNOWN_CHAIN");
        }

        StorageInterface.OpenLimitOrder[]
            memory openLimitOrders = IStateCopyUtils(address(storageT))
                .getOpenLimitOrders();
        require(start < openLimitOrders.length, "START_TOO_BIG");

        if (end >= openLimitOrders.length) {
            end = openLimitOrders.length - 1;
        }

        for (uint i = start; i <= end; ) {
            StorageInterface.OpenLimitOrder memory o = openLimitOrders[i];
            openLimitOrderTypes[o.trader][o.pairIndex][
                o.index
            ] = OpenLimitOrderType(
                uint(old.openLimitOrderTypes(o.trader, o.pairIndex, o.index))
            );
            ++i;
        }
    }

    function setStateCopyAsDone() external onlyGov {
        stateCopied = true;
    }

    // Manage params
    function updateTriggerTimeout(uint _triggerTimeout) external onlyGov {
        require(_triggerTimeout >= MIN_TRIGGER_TIMEOUT, "BELOW_MIN");
        triggerTimeout = _triggerTimeout;
        emit NumberUpdated("triggerTimeout", _triggerTimeout);
    }

    function updateSameBlockLimit(uint _sameBlockLimit) external onlyGov {
        require(_sameBlockLimit >= MIN_SAME_BLOCK_LIMIT, "BELOW_MIN");
        require(_sameBlockLimit <= MAX_SAME_BLOCK_LIMIT, "ABOVE_MAX");

        sameBlockLimit = _sameBlockLimit;

        emit NumberUpdated("sameBlockLimit", _sameBlockLimit);
    }

    // Triggers
    function storeFirstToTrigger(
        TriggeredLimitId calldata _id,
        address _bot,
        uint _linkFee
    ) external onlyTrading {
        TriggeredLimit storage t = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];

        t.first = _bot;
        t.linkFee = uint240(_linkFee);
        t.sameBlockLimit = uint16(sameBlockLimit);

        delete t.sameBlock;
        t.block = block.number;
        t.sameBlock.push(_bot);

        emit TriggeredFirst(_id, _bot, _linkFee);
    }

    function storeTriggerSameBlock(
        TriggeredLimitId calldata _id,
        address _bot
    ) external onlyTrading {
        TriggeredLimit storage t = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];

        require(t.block == block.number, "TOO_LATE");
        require(t.sameBlock.length < t.sameBlockLimit, "SAME_BLOCK_LIMIT");

        uint linkContribution = t.linkFee / t.sameBlockLimit;

        // transfer 1/N th of the trigger link cost in exchange for an equal share of reward
        storageT.linkErc677().transferFrom(_bot, t.first, linkContribution);

        t.sameBlock.push(_bot);

        emit TriggeredSameBlock(_id, _bot, linkContribution);
    }

    function unregisterTrigger(
        TriggeredLimitId calldata _id
    ) external onlyCallbacks {
        delete triggeredLimits[_id.trader][_id.pairIndex][_id.index][_id.order];
        emit TriggerUnregistered(_id);
    }

    // Distribute rewards
    function distributeNftReward(
        TriggeredLimitId calldata _id,
        uint _reward,
        uint _tokenPriceWETH
    ) external onlyCallbacks {
        TriggeredLimit memory t = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];

        require(t.block > 0, "NOT_TRIGGERED");

        uint nextRound = currentRound + 1;
        uint linkEquivalentRewards = linkToTokenRewards(
            t.linkFee,
            _tokenPriceWETH
        ); // amount of link spent in gns

        // if we've somehow ended up with an odd rate revert to using full rewards
        if (linkEquivalentRewards > _reward) linkEquivalentRewards = _reward;

        // rewards per trigger
        uint sameBlockReward = linkEquivalentRewards / t.sameBlockLimit;

        for (uint i = 0; i < t.sameBlock.length; i++) {
            address bot = t.sameBlock[i];

            tokensToClaim[bot] += sameBlockReward; // link refund
            roundOrdersToClaim[bot][nextRound]++; // next round pool entry
        }

        uint missingSameBlocks = t.sameBlockLimit - t.sameBlock.length;
        if (missingSameBlocks > 0) {
            // reward first trigger equivalent amount of missed link refunds in gns, but no extra entries into the pool
            tokensToClaim[t.first] += sameBlockReward * missingSameBlocks;
        }

        // REWARD POOLS ARE BLIND
        // when you trigger orders you earn entries for next round
        // next round tokens can't be predicted
        // rewards are added to current round and claimable by previous round (currentRound - 1) entrants

        roundTokens[currentRound].tokens += uint240(
            _reward - linkEquivalentRewards
        );
        roundTokens[nextRound].totalEntries += uint16(t.sameBlock.length);

        storageT.handleTokens(
            address(this),
            currentRound > 0 ? _reward : linkEquivalentRewards,
            true
        );

        if (currentOrder == ROUND_LENGTH) {
            currentOrder = 1;
            currentRound++;
        } else {
            currentOrder++;
        }

        emit TriggerRewarded(
            _id,
            t.first,
            t.sameBlock.length,
            t.sameBlockLimit,
            _reward
        );
    }

    // Claim rewards
    function claimPoolTokens(uint _fromRound, uint _toRound) external {
        require(_toRound >= _fromRound, "TO_BEFORE_FROM");
        require(_toRound < currentRound, "TOO_EARLY");

        uint tokens;

        // due to blind rewards round 0 will have 0 entries; r[0] rewards are effectively burned/never minted
        for (uint i = _fromRound; i <= _toRound; i++) {
            uint roundEntries = roundOrdersToClaim[msg.sender][i];

            if (roundEntries > 0) {
                RoundDetails memory roundDetails = roundTokens[i];
                tokens +=
                    (roundEntries * roundDetails.tokens) /
                    roundDetails.totalEntries;
                roundOrdersToClaim[msg.sender][i] = 0;
            }
        }

        require(tokens > 0, "NOTHING_TO_CLAIM");
        storageT.token().transfer(msg.sender, tokens);

        tokensClaimed[msg.sender] += tokens;
        tokensClaimedTotal += tokens;

        emit PoolTokensClaimed(msg.sender, _fromRound, _toRound, tokens);
    }

    function claimTokens() external {
        uint tokens = tokensToClaim[msg.sender];
        require(tokens > 0, "NOTHING_TO_CLAIM");

        tokensToClaim[msg.sender] = 0;
        storageT.token().transfer(msg.sender, tokens);

        tokensClaimed[msg.sender] += tokens;
        tokensClaimedTotal += tokens;

        emit TokensClaimed(msg.sender, tokens);
    }

    // Manage open limit order types
    function setOpenLimitOrderType(
        address _trader,
        uint _pairIndex,
        uint _index,
        OpenLimitOrderType _type
    ) external onlyTrading {
        openLimitOrderTypes[_trader][_pairIndex][_index] = _type;
    }

    // Set bot address and NFT in use so it cannot be used in the same order twice
    function setNftBotInUse(
        bytes32 nftHash,
        bytes32 botHash
    ) external onlyTrading {
        botInUse[nftHash] = true;
        botInUse[botHash] = true;
    }

    // Getters
    function triggered(
        TriggeredLimitId calldata _id
    ) external view returns (bool) {
        TriggeredLimit memory t = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];
        return t.block > 0;
    }

    function timedOut(
        TriggeredLimitId calldata _id
    ) external view returns (bool) {
        TriggeredLimit memory t = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];
        return t.block > 0 && block.number - t.block >= triggerTimeout;
    }

    function sameBlockTriggers(
        TriggeredLimitId calldata _id
    ) external view returns (address[] memory) {
        return
            triggeredLimits[_id.trader][_id.pairIndex][_id.index][_id.order]
                .sameBlock;
    }

    function getNftBotHashes(
        uint triggerBlock,
        address bot,
        uint nftId,
        address trader,
        uint pairIndex,
        uint index
    ) external pure returns (bytes32, bytes32) {
        return (
            keccak256(
                abi.encodePacked(
                    "N",
                    triggerBlock,
                    nftId,
                    trader,
                    pairIndex,
                    index
                )
            ),
            keccak256(
                abi.encodePacked(
                    "B",
                    triggerBlock,
                    bot,
                    trader,
                    pairIndex,
                    index
                )
            )
        );
    }

    function nftBotInUse(
        bytes32 nftHash,
        bytes32 botHash
    ) external view returns (bool) {
        return botInUse[nftHash] || botInUse[botHash];
    }

    function linkToTokenRewards(
        uint linkFee,
        uint tokenPrice
    ) public view returns (uint) {
        (, int linkPriceUsd, , , ) = AggregatorInterfaceV1_3(
            address(storageT.priceAggregator())
        ).linkPriceFeed().latestRoundData();
        return (linkFee * uint(linkPriceUsd) * PRECISION) / tokenPrice / 1e8;
    }
}

contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "./interfaces/TokenInterface.sol";
import "./interfaces/NftInterfaceV5.sol";

contract Staking {
    // Contracts & Addresses
    address public govFund;

    TokenInterface public immutable token; // GNS
    TokenInterface public immutable WETH;

    NftInterfaceV5[5] public nfts;

    // Pool state
    uint public accWETHPerToken;
    uint public tokenBalance;

    // Pool parameters
    uint[5] public boostsP;
    uint public maxNftsStaked;

    // Pool stats
    uint public totalRewardsDistributedWETH; // 1e18

    // Mappings
    mapping(address => User) public users;
    mapping(address => mapping(uint => StakedNft)) public userNfts;

    // Structs
    struct StakedNft {
        uint nftId;
        uint nftType;
    }
    struct User {
        uint stakedTokens; // 1e18
        uint debtWETH; // 1e18
        uint stakedNftsCount;
        uint totalBoostTokens; // 1e18
        uint harvestedRewardsWETH; // 1e18
    }

    // Events
    event GovFundUpdated(address value);
    event BoostsUpdated(uint[5] boosts);
    event MaxNftsStakedUpdated(uint value);

    event WETHDistributed(uint amount);

    event WETHHarvested(address indexed user, uint amount);

    event TokensStaked(address indexed user, uint amount);
    event TokensUnstaked(address indexed user, uint amount);

    event NftStaked(address indexed user, uint indexed nftType, uint nftId);
    event NftUnstaked(address indexed user, uint indexed nftType, uint nftId);

    constructor(
        address _govFund,
        TokenInterface _token,
        TokenInterface _WETH,
        NftInterfaceV5[5] memory _nfts,
        uint[5] memory _boostsP,
        uint _maxNftsStaked
    ) {
        require(
            _govFund != address(0) &&
                address(_token) != address(0) &&
                address(_WETH) != address(0) &&
                address(_nfts[4]) != address(0),
            "WRONG_PARAMS"
        );

        checkBoostsP(_boostsP);

        govFund = _govFund;
        token = _token;
        WETH = _WETH;
        nfts = _nfts;

        boostsP = _boostsP;
        maxNftsStaked = _maxNftsStaked;
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == govFund, "GOV_ONLY");
        _;
    }
    modifier notContract() {
        require(tx.origin == msg.sender, "CONTRACT");
        _;
    }

    // Manage addresses
    function setGovFund(address value) external onlyGov {
        require(value != address(0), "ADDRESS_0");

        govFund = value;

        emit GovFundUpdated(value);
    }

    // Manage parameters
    function checkBoostsP(uint[5] memory value) public pure {
        require(
            value[0] < value[1] &&
                value[1] < value[2] &&
                value[2] < value[3] &&
                value[3] < value[4],
            "WRONG_VALUES"
        );
    }

    function setBoostsP(uint[5] memory value) external onlyGov {
        checkBoostsP(value);

        boostsP = value;

        emit BoostsUpdated(value);
    }

    function setMaxNftsStaked(uint value) external onlyGov {
        maxNftsStaked = value;

        emit MaxNftsStakedUpdated(value);
    }

    // Distribute rewards
    function distributeRewardWETH(uint amount) external {
        WETH.transferFrom(msg.sender, address(this), amount);

        if (tokenBalance > 0) {
            accWETHPerToken += (amount * 1e18) / tokenBalance;
            totalRewardsDistributedWETH += amount;
        }

        emit WETHDistributed(amount);
    }

    // Compute user boosts
    function setBoosts() private {
        User storage u = users[msg.sender];

        u.totalBoostTokens = 0;

        for (uint i = 0; i < u.stakedNftsCount; i++) {
            u.totalBoostTokens +=
                (u.stakedTokens *
                    boostsP[userNfts[msg.sender][i].nftType - 1]) /
                100;
        }

        u.debtWETH =
            ((u.stakedTokens + u.totalBoostTokens) * accWETHPerToken) /
            1e18;
    }

    // Rewards to be harvested
    function pendingRewardWETH() public view returns (uint) {
        User storage u = users[msg.sender];

        return
            ((u.stakedTokens + u.totalBoostTokens) * accWETHPerToken) /
            1e18 -
            u.debtWETH;
    }

    // Harvest rewards
    function harvest() public {
        uint pendingWETH = pendingRewardWETH();

        User storage u = users[msg.sender];
        u.debtWETH =
            ((u.stakedTokens + u.totalBoostTokens) * accWETHPerToken) /
            1e18;
        u.harvestedRewardsWETH += pendingWETH;

        WETH.transfer(msg.sender, pendingWETH);

        emit WETHHarvested(msg.sender, pendingWETH);
    }

    // Stake tokens
    function stakeTokens(uint amount) external {
        User storage u = users[msg.sender];

        token.transferFrom(msg.sender, address(this), amount);

        harvest();

        tokenBalance -= (u.stakedTokens + u.totalBoostTokens);

        u.stakedTokens += amount;
        setBoosts();

        tokenBalance += (u.stakedTokens + u.totalBoostTokens);

        emit TokensStaked(msg.sender, amount);
    }

    // Unstake tokens
    function unstakeTokens(uint amount) external {
        User storage u = users[msg.sender];

        harvest();

        tokenBalance -= (u.stakedTokens + u.totalBoostTokens);

        u.stakedTokens -= amount;
        setBoosts();

        tokenBalance += (u.stakedTokens + u.totalBoostTokens);

        token.transfer(msg.sender, amount);

        emit TokensUnstaked(msg.sender, amount);
    }

    // Stake NFT
    // NFT types: 1, 2, 3, 4, 5
    function stakeNft(uint nftType, uint nftId) external notContract {
        User storage u = users[msg.sender];

        require(u.stakedNftsCount < maxNftsStaked, "MAX_NFTS_ALREADY_STAKED");

        nfts[nftType - 1].transferFrom(msg.sender, address(this), nftId);

        harvest();

        tokenBalance -= (u.stakedTokens + u.totalBoostTokens);

        StakedNft storage stakedNft = userNfts[msg.sender][u.stakedNftsCount++];
        stakedNft.nftType = nftType;
        stakedNft.nftId = nftId;

        setBoosts();

        tokenBalance += (u.stakedTokens + u.totalBoostTokens);

        emit NftStaked(msg.sender, nftType, nftId);
    }

    // Unstake NFT
    function unstakeNft(uint nftIndex) external {
        User storage u = users[msg.sender];
        StakedNft memory stakedNft = userNfts[msg.sender][nftIndex];

        harvest();

        tokenBalance -= (u.stakedTokens + u.totalBoostTokens);

        userNfts[msg.sender][nftIndex] = userNfts[msg.sender][
            u.stakedNftsCount - 1
        ];
        delete userNfts[msg.sender][(u.stakedNftsCount--) - 1];

        setBoosts();

        tokenBalance += (u.stakedTokens + u.totalBoostTokens);

        nfts[stakedNft.nftType - 1].transferFrom(
            address(this),
            msg.sender,
            stakedNft.nftId
        );

        emit NftUnstaked(msg.sender, stakedNft.nftType, stakedNft.nftId);
    }
}

contracts/Storage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "./interfaces/StorageInterface.sol";
import "./interfaces/AggregatorInterfaceV1.sol";
import "./interfaces/PoolInterfaceV5.sol";
import "./interfaces/NftInterfaceV5.sol";
import "./interfaces/PausableInterfaceV5.sol";

contract Storage is StorageInterface {
    // Constants
    uint public constant PRECISION = 1e10;
    bytes32 public constant MINTER_ROLE =
        0x9f2df0fed2c77648de5860a4cc508cd0818c85b8b8a1ab4ceeef8d981c8956a6;
    TokenInterface public WETH;
    IOracle public oracle;
    TokenInterface public constant linkErc677 =
        TokenInterface(0xb0897686c545045aFc77CF20eC7A532E3120E0F1);

    // Contracts (updatable)
    AggregatorInterfaceV1 public priceAggregator;
    PoolInterfaceV5 public pool;
    PausableInterfaceV5 public trading;
    PausableInterfaceV5 public callbacks;
    TokenInterface public token =
        TokenInterface(0x7075cAB6bCCA06613e2d071bd918D1a0241379E2);
    NftInterfaceV5[5] public nfts = [
        NftInterfaceV5(0xF9A4c522E327935BD1F5a338c121E14e4cc1f898),
        NftInterfaceV5(0x77cd42B925e1A82f41d852D6BE727CFc88fddBbC),
        NftInterfaceV5(0x3378AD81D09DE23725Ee9B9270635c97Ed601921),
        NftInterfaceV5(0x02e2c5825C1a3b69C0417706DbE1327C2Af3e6C2),
        NftInterfaceV5(0x2D266A94469d05C9e06D52A4D0d9C23b157767c2)
    ];
    address public vault;
    address public tokenWETHRouter;

    // Trading variables
    uint public maxTradesPerPair = 3;
    uint public maxTradesPerBlock = 5;
    uint public maxPendingMarketOrders = 5;
    uint public maxGainP = 900; // %
    uint public maxSlP = 80; // %
    uint public defaultLeverageUnlocked = 50; // x
    uint public nftSuccessTimelock = 50; // 50 blocks
    uint[5] public spreadReductionsP = [15, 20, 25, 30, 35]; // %

    // Gov & dev addresses (updatable)
    address public gov; //TODO need to change to our
    address public dev; //TODO need to change to our

    // Gov & dev fees
    uint public devFeesToken; // 1e18
    uint public devFeesWETH; // 1e18
    uint public govFeesToken; // 1e18
    uint public govFeesWETH; // 1e18

    // Stats
    uint public tokensBurned; // 1e18
    uint public tokensMinted; // 1e18
    uint public nftRewards; // 1e18

    // Enums
    // enum LimitOrder {
    //     TP,
    //     SL,
    //     LIQ,
    //     OPEN
    // }

    // Structs
    struct Trader {
        uint leverageUnlocked;
        address referral;
        uint referralRewardsTotal; // 1e18
    }
    // struct Trade {
    //     address trader;
    //     uint pairIndex;
    //     uint index;
    //     uint initialPosToken; // 1e18
    //     uint positionSizeWETH; // 1e18
    //     uint openPrice; // PRECISION
    //     bool buy;
    //     uint leverage;
    //     uint tp; // PRECISION
    //     uint sl; // PRECISION
    // }
    // struct TradeInfo {
    //     uint tokenId;
    //     uint tokenPriceWETH; // PRECISION
    //     uint openInterestWETH; // 1e18
    //     uint tpLastUpdated;
    //     uint slLastUpdated;
    //     bool beingMarketClosed;
    // }
    // struct OpenLimitOrder {
    //     address trader;
    //     uint pairIndex;
    //     uint index;
    //     uint positionSize; // 1e18 (WETH or GFARM2)
    //     uint spreadReductionP;
    //     bool buy;
    //     uint leverage;
    //     uint tp; // PRECISION (%)
    //     uint sl; // PRECISION (%)
    //     uint minPrice; // PRECISION
    //     uint maxPrice; // PRECISION
    //     uint block;
    //     uint tokenId; // index in supportedTokens
    // }
    // struct PendingMarketOrder {
    //     Trade trade;
    //     uint block;
    //     uint wantedPrice; // PRECISION
    //     uint slippageP; // PRECISION (%)
    //     uint spreadReductionP;
    //     uint tokenId; // index in supportedTokens
    // }
    // struct PendingNftOrder {
    //     address nftHolder;
    //     uint nftId;
    //     address trader;
    //     uint pairIndex;
    //     uint index;
    //     LimitOrder orderType;
    // }

    // Supported tokens to open trades with
    address[] public supportedTokens;

    // User info mapping
    mapping(address => Trader) public traders;

    // Trades mappings
    mapping(address => mapping(uint => mapping(uint => Trade)))
        public openTrades;
    mapping(address => mapping(uint => mapping(uint => TradeInfo)))
        public openTradesInfo;
    mapping(address => mapping(uint => uint)) public openTradesCount;

    // Limit orders mappings
    mapping(address => mapping(uint => mapping(uint => uint)))
        public openLimitOrderIds;
    mapping(address => mapping(uint => uint)) public openLimitOrdersCount;
    OpenLimitOrder[] public openLimitOrders;

    // Pending orders mappings
    mapping(uint => PendingMarketOrder) public reqID_pendingMarketOrder;
    mapping(uint => PendingNftOrder) public reqID_pendingNftOrder;
    mapping(address => uint[]) public pendingOrderIds;
    mapping(address => mapping(uint => uint)) public pendingMarketOpenCount;
    mapping(address => mapping(uint => uint)) public pendingMarketCloseCount;

    // List of open trades & limit orders
    mapping(uint => address[]) public pairTraders;
    mapping(address => mapping(uint => uint)) public pairTradersId;

    // Current and max open interests for each pair
    mapping(uint => uint[3]) public openInterestWETH; // 1e18 [long,short,max]

    // Restrictions & Timelocks
    mapping(uint => uint) public tradesPerBlock;
    mapping(uint => uint) public nftLastSuccess;

    // List of allowed contracts => can update storage + mint/burn tokens
    mapping(address => bool) public isTradingContract;

    // Events
    event SupportedTokenAdded(address a);
    event TradingContractAdded(address a);
    event TradingContractRemoved(address a);
    event AddressUpdated(string name, address a);
    event NftsUpdated(NftInterfaceV5[5] nfts);
    event NumberUpdated(string name, uint value);
    event NumberUpdatedPair(string name, uint pairIndex, uint value);
    event SpreadReductionsUpdated(uint[5]);

    constructor(address _WETH, address _gov, address _dev) {
        WETH = TokenInterface(_WETH);
        gov = _gov;
        dev = _dev;
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == gov);
        _;
    }
    modifier onlyTrading() {
        require(
            isTradingContract[msg.sender],
            // &&
            //     token.hasRole(MINTER_ROLE, msg.sender)
            "here here"
        );
        _;
    }

    // Manage addresses
    function setGov(address _gov) external onlyGov {
        require(_gov != address(0));
        gov = _gov;
        emit AddressUpdated("gov", _gov);
    }

    function setDev(address _dev) external onlyGov {
        require(_dev != address(0));
        dev = _dev;
        emit AddressUpdated("dev", _dev);
    }

    function updateToken(TokenInterface _newToken) external onlyGov {
        require(trading.isPaused() && callbacks.isPaused(), "NOT_PAUSED");
        require(address(_newToken) != address(0));
        token = _newToken;
        emit AddressUpdated("token", address(_newToken));
    }

    function updateNfts(NftInterfaceV5[5] memory _nfts) external onlyGov {
        require(address(_nfts[0]) != address(0));
        nfts = _nfts;
        emit NftsUpdated(_nfts);
    }

    // Trading + callbacks contracts
    function addTradingContract(address _trading) external onlyGov {
        // require(token.hasRole(MINTER_ROLE, _trading), "NOT_MINTER"); /// TODO : need to have a gtoken type token of our own
        require(_trading != address(0));
        isTradingContract[_trading] = true;
        emit TradingContractAdded(_trading);
    }

    function removeTradingContract(address _trading) external onlyGov {
        require(_trading != address(0));
        isTradingContract[_trading] = false;
        emit TradingContractRemoved(_trading);
    }

    function addSupportedToken(address _token) external onlyGov {
        require(_token != address(0));
        supportedTokens.push(_token);
        emit SupportedTokenAdded(_token);
    }

    function setPriceAggregator(address _aggregator) external onlyGov {
        require(_aggregator != address(0));
        priceAggregator = AggregatorInterfaceV1(_aggregator);
        emit AddressUpdated("priceAggregator", _aggregator);
    }

    function setPool(address _pool) external onlyGov {
        require(_pool != address(0));
        pool = PoolInterfaceV5(_pool);
        emit AddressUpdated("pool", _pool);
    }

    function setVault(address _vault) external onlyGov {
        require(_vault != address(0));
        vault = _vault;
        emit AddressUpdated("vault", _vault);
    }

    function setTrading(address _trading) external onlyGov {
        require(_trading != address(0));
        trading = PausableInterfaceV5(_trading);
        emit AddressUpdated("trading", _trading);
    }

    function setCallbacks(address _callbacks) external onlyGov {
        require(_callbacks != address(0));
        callbacks = PausableInterfaceV5(_callbacks);
        emit AddressUpdated("callbacks", _callbacks);
    }

    function setTokenWETHRouter(address _tokenWETHRouter) external onlyGov {
        require(_tokenWETHRouter != address(0));
        tokenWETHRouter = _tokenWETHRouter;
        emit AddressUpdated("tokenWETHRouter", _tokenWETHRouter);
    }

    // Manage trading variables
    function setMaxTradesPerBlock(uint _maxTradesPerBlock) external onlyGov {
        require(_maxTradesPerBlock > 0);
        maxTradesPerBlock = _maxTradesPerBlock;
        emit NumberUpdated("maxTradesPerBlock", _maxTradesPerBlock);
    }

    function setMaxTradesPerPair(uint _maxTradesPerPair) external onlyGov {
        require(_maxTradesPerPair > 0);
        maxTradesPerPair = _maxTradesPerPair;
        emit NumberUpdated("maxTradesPerPair", _maxTradesPerPair);
    }

    function setMaxPendingMarketOrders(
        uint _maxPendingMarketOrders
    ) external onlyGov {
        require(_maxPendingMarketOrders > 0);
        maxPendingMarketOrders = _maxPendingMarketOrders;
        emit NumberUpdated("maxPendingMarketOrders", _maxPendingMarketOrders);
    }

    function setMaxGainP(uint _max) external onlyGov {
        require(_max >= 300);
        maxGainP = _max;
        emit NumberUpdated("maxGainP", _max);
    }

    function setDefaultLeverageUnlocked(uint _lev) external onlyGov {
        require(_lev > 0);
        defaultLeverageUnlocked = _lev;
        emit NumberUpdated("defaultLeverageUnlocked", _lev);
    }

    function setMaxSlP(uint _max) external onlyGov {
        require(_max >= 50);
        maxSlP = _max;
        emit NumberUpdated("maxSlP", _max);
    }

    function setNftSuccessTimelock(uint _blocks) external onlyGov {
        nftSuccessTimelock = _blocks;
        emit NumberUpdated("nftSuccessTimelock", _blocks);
    }

    function setSpreadReductionsP(uint[5] calldata _r) external onlyGov {
        require(
            _r[0] > 0 &&
                _r[1] > _r[0] &&
                _r[2] > _r[1] &&
                _r[3] > _r[2] &&
                _r[4] > _r[3]
        );
        spreadReductionsP = _r;
        emit SpreadReductionsUpdated(_r);
    }

    function setMaxOpenInterestWETH(
        uint _pairIndex,
        uint _newMaxOpenInterest
    ) external onlyGov {
        // Can set max open interest to 0 to pause trading on this pair only
        openInterestWETH[_pairIndex][2] = _newMaxOpenInterest;
        emit NumberUpdatedPair(
            "maxOpenInterestWETH",
            _pairIndex,
            _newMaxOpenInterest
        );
    }

    // Manage stored trades
    function storeTrade(
        Trade memory _trade,
        TradeInfo memory _tradeInfo
    ) external onlyTrading {
        _trade.index = firstEmptyTradeIndex(_trade.trader, _trade.pairIndex);
        openTrades[_trade.trader][_trade.pairIndex][_trade.index] = _trade;

        openTradesCount[_trade.trader][_trade.pairIndex]++;
        tradesPerBlock[block.number]++;

        if (openTradesCount[_trade.trader][_trade.pairIndex] == 1) {
            pairTradersId[_trade.trader][_trade.pairIndex] = pairTraders[
                _trade.pairIndex
            ].length;
            pairTraders[_trade.pairIndex].push(_trade.trader);
        }

        _tradeInfo.beingMarketClosed = false;
        openTradesInfo[_trade.trader][_trade.pairIndex][
            _trade.index
        ] = _tradeInfo;

        updateOpenInterestWETH(
            _trade.pairIndex,
            _tradeInfo.openInterestWETH,
            true,
            _trade.buy
        );
    }

    function unregisterTrade(
        address trader,
        uint pairIndex,
        uint index
    ) external onlyTrading {
        Trade storage t = openTrades[trader][pairIndex][index];
        TradeInfo storage i = openTradesInfo[trader][pairIndex][index];
        if (t.leverage == 0) {
            return;
        }

        updateOpenInterestWETH(pairIndex, i.openInterestWETH, false, t.buy);

        if (openTradesCount[trader][pairIndex] == 1) {
            uint _pairTradersId = pairTradersId[trader][pairIndex];
            address[] storage p = pairTraders[pairIndex];

            p[_pairTradersId] = p[p.length - 1];
            pairTradersId[p[_pairTradersId]][pairIndex] = _pairTradersId;

            delete pairTradersId[trader][pairIndex];
            p.pop();
        }

        delete openTrades[trader][pairIndex][index];
        delete openTradesInfo[trader][pairIndex][index];

        openTradesCount[trader][pairIndex]--;
        tradesPerBlock[block.number]++;
    }

    // Manage pending market orders
    function storePendingMarketOrder(
        PendingMarketOrder memory _order,
        uint _id,
        bool _open
    ) external onlyTrading {
        pendingOrderIds[_order.trade.trader].push(_id);

        reqID_pendingMarketOrder[_id] = _order;
        reqID_pendingMarketOrder[_id].block = block.number;

        if (_open) {
            pendingMarketOpenCount[_order.trade.trader][
                _order.trade.pairIndex
            ]++;
        } else {
            pendingMarketCloseCount[_order.trade.trader][
                _order.trade.pairIndex
            ]++;
            openTradesInfo[_order.trade.trader][_order.trade.pairIndex][
                _order.trade.index
            ].beingMarketClosed = true;
        }
    }

    function unregisterPendingMarketOrder(
        uint _id,
        bool _open
    ) external onlyTrading {
        PendingMarketOrder memory _order = reqID_pendingMarketOrder[_id];
        uint[] storage orderIds = pendingOrderIds[_order.trade.trader];

        for (uint i = 0; i < orderIds.length; i++) {
            if (orderIds[i] == _id) {
                if (_open) {
                    pendingMarketOpenCount[_order.trade.trader][
                        _order.trade.pairIndex
                    ]--;
                } else {
                    pendingMarketCloseCount[_order.trade.trader][
                        _order.trade.pairIndex
                    ]--;
                    openTradesInfo[_order.trade.trader][_order.trade.pairIndex][
                        _order.trade.index
                    ].beingMarketClosed = false;
                }

                orderIds[i] = orderIds[orderIds.length - 1];
                orderIds.pop();

                delete reqID_pendingMarketOrder[_id];
                return;
            }
        }
    }

    // Manage open interest
    function updateOpenInterestWETH(
        uint _pairIndex,
        uint _leveragedPosWETH,
        bool _open,
        bool _long
    ) private {
        uint index = _long ? 0 : 1;
        uint[3] storage o = openInterestWETH[_pairIndex];
        o[index] = _open
            ? o[index] + _leveragedPosWETH
            : o[index] - _leveragedPosWETH;
    }

    // Manage open limit orders
    function storeOpenLimitOrder(OpenLimitOrder memory o) external onlyTrading {
        o.index = firstEmptyOpenLimitIndex(o.trader, o.pairIndex);
        o.block = block.number;
        openLimitOrders.push(o);
        openLimitOrderIds[o.trader][o.pairIndex][o.index] =
            openLimitOrders.length -
            1;
        openLimitOrdersCount[o.trader][o.pairIndex]++;
    }

    function updateOpenLimitOrder(
        OpenLimitOrder calldata _o
    ) external onlyTrading {
        if (!hasOpenLimitOrder(_o.trader, _o.pairIndex, _o.index)) {
            return;
        }
        OpenLimitOrder storage o = openLimitOrders[
            openLimitOrderIds[_o.trader][_o.pairIndex][_o.index]
        ];
        o.positionSize = _o.positionSize;
        o.buy = _o.buy;
        o.leverage = _o.leverage;
        o.tp = _o.tp;
        o.sl = _o.sl;
        o.minPrice = _o.minPrice;
        o.maxPrice = _o.maxPrice;
        o.block = block.number;
    }

    function unregisterOpenLimitOrder(
        address _trader,
        uint _pairIndex,
        uint _index
    ) external onlyTrading {
        if (!hasOpenLimitOrder(_trader, _pairIndex, _index)) {
            return;
        }

        // Copy last order to deleted order => update id of this limit order
        uint id = openLimitOrderIds[_trader][_pairIndex][_index];
        openLimitOrders[id] = openLimitOrders[openLimitOrders.length - 1];
        openLimitOrderIds[openLimitOrders[id].trader][
            openLimitOrders[id].pairIndex
        ][openLimitOrders[id].index] = id;

        // Remove
        delete openLimitOrderIds[_trader][_pairIndex][_index];
        openLimitOrders.pop();

        openLimitOrdersCount[_trader][_pairIndex]--;
    }

    // Manage NFT orders
    function storePendingNftOrder(
        PendingNftOrder memory _nftOrder,
        uint _orderId
    ) external onlyTrading {
        reqID_pendingNftOrder[_orderId] = _nftOrder;
    }

    function unregisterPendingNftOrder(uint _order) external onlyTrading {
        delete reqID_pendingNftOrder[_order];
    }

    // Manage open trade
    function updateSl(
        address _trader,
        uint _pairIndex,
        uint _index,
        uint _newSl
    ) external onlyTrading {
        Trade storage t = openTrades[_trader][_pairIndex][_index];
        TradeInfo storage i = openTradesInfo[_trader][_pairIndex][_index];
        if (t.leverage == 0) {
            return;
        }
        t.sl = _newSl;
        i.slLastUpdated = block.number;
    }

    function updateTp(
        address _trader,
        uint _pairIndex,
        uint _index,
        uint _newTp
    ) external onlyTrading {
        Trade storage t = openTrades[_trader][_pairIndex][_index];
        TradeInfo storage i = openTradesInfo[_trader][_pairIndex][_index];
        if (t.leverage == 0) {
            return;
        }
        t.tp = _newTp;
        i.tpLastUpdated = block.number;
    }

    function updateTrade(Trade memory _t) external onlyTrading {
        // useful when partial adding/closing
        Trade storage t = openTrades[_t.trader][_t.pairIndex][_t.index];
        if (t.leverage == 0) {
            return;
        }
        t.initialPosToken = _t.initialPosToken;
        t.positionSizeWETH = _t.positionSizeWETH;
        t.openPrice = _t.openPrice;
        t.leverage = _t.leverage;
    }

    // Manage referrals
    function storeReferral(
        address _trader,
        address _referral
    ) external onlyTrading {
        Trader storage trader = traders[_trader];
        trader.referral = _referral != address(0) &&
            trader.referral == address(0) &&
            _referral != _trader
            ? _referral
            : trader.referral;
    }

    function increaseReferralRewards(
        address _referral,
        uint _amount
    ) external onlyTrading {
        traders[_referral].referralRewardsTotal += _amount;
    }

    // Manage rewards
    function distributeLpRewards(uint _amount) external onlyTrading {
        pool.increaseAccTokensPerLp(_amount);
    }

    function increaseNftRewards(
        uint _nftId,
        uint _amount
    ) external onlyTrading {
        nftLastSuccess[_nftId] = block.number;
        nftRewards += _amount;
    }

    // Unlock next leverage
    function setLeverageUnlocked(
        address _trader,
        uint _newLeverage
    ) external onlyTrading {
        traders[_trader].leverageUnlocked = _newLeverage;
    }

    // Manage dev & gov fees
    function handleDevGovFees(
        uint _pairIndex,
        uint _leveragedPositionSize,
        bool _WETH,
        bool _fullFee
    ) external onlyTrading returns (uint fee) {
        fee =
            (_leveragedPositionSize * priceAggregator.openFeeP(_pairIndex)) /
            PRECISION /
            100;
        if (!_fullFee) {
            fee /= 2;
        }

        if (_WETH) {
            govFeesWETH += fee;
            devFeesWETH += fee;
        } else {
            govFeesToken += fee;
            devFeesToken += fee;
        }

        fee *= 2;
    }

    function claimFees() external onlyGov {
        token.mint(dev, devFeesToken);
        token.mint(gov, govFeesToken);

        tokensMinted += devFeesToken + govFeesToken;

        WETH.transfer(gov, govFeesWETH);
        WETH.transfer(dev, devFeesWETH);

        devFeesToken = 0;
        govFeesToken = 0;
        devFeesWETH = 0;
        govFeesWETH = 0;
    }

    // Manage tokens
    function handleTokens(
        address _a,
        uint _amount,
        bool _mint
    ) external onlyTrading {
        if (_mint) {
            token.mint(_a, _amount);
            tokensMinted += _amount;
        } else {
            token.burn(_a, _amount);
            tokensBurned += _amount;
        }
    }

    function transferWETH(
        address _from,
        address _to,
        uint _amount
    ) external onlyTrading {
        if (_from == address(this)) {
            WETH.transfer(_to, _amount);
        } else {
            WETH.transferFrom(_from, _to, _amount);
        }
    }

    function mintWETH(address _to, uint _amount) external onlyTrading {
        WETH.mint(_to, _amount);
    }

    function transferLinkToAggregator(
        address _from,
        uint _pairIndex,
        uint _leveragedPosWETH
    ) external onlyTrading {
        linkErc677.transferFrom(
            _from,
            address(priceAggregator),
            priceAggregator.linkFee(_pairIndex, _leveragedPosWETH)
        );
    }

    // View utils functions
    function firstEmptyTradeIndex(
        address trader,
        uint pairIndex
    ) public view returns (uint index) {
        for (uint i = 0; i < maxTradesPerPair; i++) {
            if (openTrades[trader][pairIndex][i].leverage == 0) {
                index = i;
                break;
            }
        }
    }

    function firstEmptyOpenLimitIndex(
        address trader,
        uint pairIndex
    ) public view returns (uint index) {
        for (uint i = 0; i < maxTradesPerPair; i++) {
            if (!hasOpenLimitOrder(trader, pairIndex, i)) {
                index = i;
                break;
            }
        }
    }

    function hasOpenLimitOrder(
        address trader,
        uint pairIndex,
        uint index
    ) public view returns (bool) {
        if (openLimitOrders.length == 0) {
            return false;
        }
        OpenLimitOrder storage o = openLimitOrders[
            openLimitOrderIds[trader][pairIndex][index]
        ];
        return
            o.trader == trader && o.pairIndex == pairIndex && o.index == index;
    }

    // Additional getters
    function getReferral(address _trader) external view returns (address) {
        return traders[_trader].referral;
    }

    function getLeverageUnlocked(address _trader) external view returns (uint) {
        return traders[_trader].leverageUnlocked;
    }

    function pairTradersArray(
        uint _pairIndex
    ) external view returns (address[] memory) {
        return pairTraders[_pairIndex];
    }

    function getPendingOrderIds(
        address _trader
    ) external view returns (uint[] memory) {
        return pendingOrderIds[_trader];
    }

    function pendingOrderIdsCount(
        address _trader
    ) external view returns (uint) {
        return pendingOrderIds[_trader].length;
    }

    function getOpenLimitOrder(
        address _trader,
        uint _pairIndex,
        uint _index
    ) external view returns (OpenLimitOrder memory) {
        require(hasOpenLimitOrder(_trader, _pairIndex, _index));
        return openLimitOrders[openLimitOrderIds[_trader][_pairIndex][_index]];
    }

    function getOpenLimitOrders()
        external
        view
        returns (OpenLimitOrder[] memory)
    {
        return openLimitOrders;
    }

    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokens;
    }

    function getSpreadReductionsArray() external view returns (uint[5] memory) {
        return spreadReductionsP;
    }

    function getGov() external view returns (address _gov) {
        return gov;
    }

    function setOracle(address _oracle) external {
        oracle = IOracle(_oracle);
    }

    function getOpenTrades(
        address trader,
        uint pairIndex,
        uint index
    ) external view returns (Trade memory) {
        return openTrades[trader][pairIndex][index];
    }

    function getOpenTradesInfo(
        address trader,
        uint pairIndex,
        uint index
    ) external view returns (TradeInfo memory) {
        return openTradesInfo[trader][pairIndex][index];
    }

    function getPendingMarketOrder(
        uint orderId
    ) external view returns (PendingMarketOrder memory) {
        return reqID_pendingMarketOrder[orderId];
    }

    function getPendingNftOrder(
        uint orderId
    ) external view returns (PendingNftOrder memory) {
        return reqID_pendingNftOrder[orderId];
    }
}

