    //         price,
    //         tp,
    //         sl,
    //         maxSlippageP
    //     );
    // }

    // function cancelOpenLimitOrder(
    //     uint pairIndex,
    //     uint index
    // ) external notContract notDone {
    //     address sender = _msgSender();
    //     require(
    //         storageT.hasOpenLimitOrder(sender, pairIndex, index),
    //         "NO_LIMIT"
    //     );

    //     StorageInterface.OpenLimitOrder memory o = storageT.getOpenLimitOrder(
    //         sender,
