    //     );

    //     storageT.unregisterOpenLimitOrder(sender, pairIndex, index);
    //     storageT.transferWETH(address(storageT), sender, o.positionSize);

    //     emit OpenLimitCanceled(sender, pairIndex, index);
    // }

    // Manage limit order (TP/SL)
    function updateTp(
        uint pairIndex,
        uint index,
        uint newTp
    ) external notContract notDone {
        address sender = _msgSender();

        // checkNoPendingTrigger(
        //     sender,
        //     pairIndex,
        //     index,
        //     StorageInterface.LimitOrder.TP
        // );
        StorageInterface.Trade memory t = storageT.getOpenTrades(sender, pairIndex, index);
        require(t.leverage > 0, "NO_TRADE");

        storageT.updateTp(sender, pairIndex, index, newTp);
        address(storageT.callbacks()).setTpLastUpdated(
            sender,
            pairIndex,
            index,
            TradingCallbacksInterface.TradeType.MARKET,
            ChainUtils.getBlockNumber()
        );

        emit TpUpdated(sender, pairIndex, index, newTp);
    }

    function updateSl(
        uint pairIndex,
        uint index,
        uint newSl
    ) external notContract notDone {
        address sender = _msgSender();

        // checkNoPendingTrigger(
        //     sender,
        //     pairIndex,
        //     index,
        //     StorageInterface.LimitOrder.SL
        // );

        StorageInterface.Trade memory t = storageT.getOpenTrades(sender, pairIndex, index);
        require(t.leverage > 0, "NO_TRADE");

        uint maxSlDist = (t.openPrice * MAX_SL_P) / 100 / t.leverage;

        require(
            newSl == 0 ||
                (
                    t.buy
                        ? newSl >= t.openPrice - maxSlDist
                        : newSl <= t.openPrice + maxSlDist
                ),
            "SL_TOO_BIG"
        );

        storageT.updateSl(sender, pairIndex, index, newSl);
        address(storageT.callbacks()).setSlLastUpdated(
            sender,
            pairIndex,
            index,
            TradingCallbacksInterface.TradeType.MARKET,
            ChainUtils.getBlockNumber()
        );

        emit SlUpdated(sender, pairIndex, index, newSl);
    }

    // Execute limit order
    function executeNftOrder(
        uint _orderType,
        address trader,
        uint pairIndex,
        uint index,
        uint nftId,
        uint nftType
    ) external notContract notDone {
        // (
        //     uint _orderType,
        //     address trader,
        //     uint pairIndex,
        //     uint index,
        //     uint nftId,
        //     uint nftType
        // ) = packed.unpackExecuteNftOrder();
        StorageInterface.LimitOrder orderType = StorageInterface.LimitOrder(
            _orderType
        );
        address sender = _msgSender();

        // require(nftType >= 1 && nftType <= 5, "WRONG_NFT_TYPE");
        // require(storageT.nfts(nftType - 1).ownerOf(nftId) == sender, "NO_NFT");

        // require(
        //     block.number >=
        //         storageT.nftLastSuccess(nftId) + storageT.nftSuccessTimelock(),
        //     "SUCCESS_TIMELOCK"
        // );

        bool isOpenLimit = orderType == StorageInterface.LimitOrder.OPEN;
        TradingCallbacksInterface.TradeType tradeType = isOpenLimit
            ? TradingCallbacksInterface.TradeType.LIMIT
            : TradingCallbacksInterface.TradeType.MARKET;

        // require(
        //     canExecute(
        //         orderType,
        //         TradingCallbacksInterface.SimplifiedTradeId(
        //             trader,
        //             pairIndex,
        //             index,
        //             tradeType
        //         )
        //     ),
        //     "IN_TIMEOUT"
        // );

        // handleBotInUse(sender, nftId, trader, pairIndex, index);

        StorageInterface.Trade memory t;

        if (isOpenLimit) {
            revert("Open Limit orders not supported yet");
            // require(
            //     storageT.hasOpenLimitOrder(trader, pairIndex, index),
            //     "NO_LIMIT"
            // );
        } else {
            t = storageT.getOpenTrades(trader, pairIndex, index);

            require(t.leverage > 0, "NO_TRADE");

            if (orderType == StorageInterface.LimitOrder.LIQ) {
                uint liqPrice = borrowingFees.getTradeLiquidationPrice(
                    BorrowingFeesInterface.LiqPriceInput(
                        t.trader,
                        t.pairIndex,
                        t.index,
                        t.openPrice,
                        t.buy,
                        t.positionSizeWETH,
                        t.leverage
                    )
                );

                require(
                    t.sl == 0 || (t.buy ? liqPrice > t.sl : liqPrice < t.sl),
                    "HAS_SL"
                );
            } else if (orderType == StorageInterface.LimitOrder.PAR_LIQ) {
                uint parLiqPrice = borrowingFees
                    .getTradePartialLiquidationPrice(
                        BorrowingFeesInterface.LiqPriceInput(
                            t.trader,
                            t.pairIndex,
                            t.index,
                            t.openPrice,
                            t.buy,
                            t.positionSizeWETH,
                            t.leverage
                        )
                    );

                require(
                    t.sl == 0 ||
                        (t.buy ? parLiqPrice > t.sl : parLiqPrice < t.sl),
                    "HAS_SL"
                );
                int256 pnl = callbacks.getTradePnl(
                    t.trader,
                    t.pairIndex,
                    t.index
                );
                int256 position = int(t.positionSizeWETH) + pnl;
                require(
                    position >= maxLeveragedPosWETH,
                    "position to small for partial liquidation"
                );
            } else {
                require(
                    orderType != StorageInterface.LimitOrder.SL || t.sl > 0,
                    "NO_SL"
                );
                require(
                    orderType != StorageInterface.LimitOrder.TP || t.tp > 0,
                    "NO_TP"
                );
            }
        }

        // NftRewardsInterfaceV6_3_1.TriggeredLimitId
        //     memory triggeredLimitId = NftRewardsInterfaceV6_3_1
        //         .TriggeredLimitId(trader, pairIndex, index, orderType);

        if (
            true
            // !nftRewards.triggered(triggeredLimitId) ||
            // nftRewards.timedOut(triggeredLimitId)
        ) {
            uint leveragedPosWETH;

            if (isOpenLimit) {
                // StorageInterface.OpenLimitOrder memory l = storageT
                //     .getOpenLimitOrder(trader, pairIndex, index);
                // leveragedPosWETH = l.positionSize * l.leverage;
                // (uint priceImpactP, ) = pairInfos.getTradePriceImpact(
                //     0,
                //     l.pairIndex,
                //     l.buy,
                //     leveragedPosWETH
                // );
                // require(
                //     priceImpactP * l.leverage <=
                //         pairInfos.maxNegativePnlOnOpenP(),
                //     "PRICE_IMPACT_TOO_HIGH"
                // );
            } else {
                leveragedPosWETH = t.positionSizeWETH * t.leverage;
            }

            // storageT.transferLinkToAggregator(
            //     sender,
            //     pairIndex,
            //     leveragedPosWETH
            // );

            (uint orderId /*uint linkFee*/, ) = getPriceNftOrder(
                isOpenLimit,
                trader,
                pairIndex,
                index,
                tradeType,
                orderType,
                leveragedPosWETH
            );

            StorageInterface.PendingNftOrder memory pendingNftOrder;
            pendingNftOrder.nftHolder = sender;
            pendingNftOrder.nftId = nftId;
            pendingNftOrder.trader = trader;
            pendingNftOrder.pairIndex = pairIndex;
            pendingNftOrder.index = index;
            pendingNftOrder.orderType = orderType;
            storageT.storePendingNftOrder(pendingNftOrder, orderId);

            (storageT.priceAggregator()).nftOrderfulfill(
                orderId,
                pendingNftOrder
            );
            emit NftOrderInitiated(orderId, sender, trader, pairIndex);
        } else {
            // nftRewards.storeTriggerSameBlock(triggeredLimitId, sender);

            emit NftOrderSameBlock(sender, trader, pairIndex);
        }
    }

    // Market timeout
    // function openTradeMarketTimeout(uint _order) external notContract notDone {
    //     address sender = _msgSender();

    //     StorageInterface.PendingMarketOrder memory o = storageT
    //         .reqID_pendingMarketOrder(_order);
    //     StorageInterface.Trade memory t = o.trade;

    //     require(
    //         o.block > 0 && block.number >= o.block + marketOrdersTimeout,
    //         "WAIT_TIMEOUT"
    //     );
    //     require(t.trader == sender, "NOT_YOUR_ORDER");
    //     require(t.leverage > 0, "WRONG_MARKET_ORDER_TYPE");

    //     storageT.unregisterPendingMarketOrder(_order, true);
    //     storageT.transferWETH(address(storageT), sender, t.positionSizeWETH);

    //     emit ChainlinkCallbackTimeout(_order, o);
    // }

    // function closeTradeMarketTimeout(uint _order) external notContract notDone {
    //     address sender = _msgSender();

    //     StorageInterface.PendingMarketOrder memory o = storageT
    //         .reqID_pendingMarketOrder(_order);
    //     StorageInterface.Trade memory t = o.trade;

    //     require(
    //         o.block > 0 && block.number >= o.block + marketOrdersTimeout,
    //         "WAIT_TIMEOUT"
    //     );
    //     require(t.trader == sender, "NOT_YOUR_ORDER");
    //     require(t.leverage == 0, "WRONG_MARKET_ORDER_TYPE");

    //     storageT.unregisterPendingMarketOrder(_order, false);

    //     (bool success, ) = address(this).delegatecall(
    //         abi.encodeWithSignature(
    //             "closeTradeMarket(uint256,uint256)",
    //             t.pairIndex,
    //             t.index
    //         )
    //     );

    //     if (!success) {
    //         emit CouldNotCloseTrade(sender, t.pairIndex, t.index);
    //     }

    //     emit ChainlinkCallbackTimeout(_order, o);
    // }

    // //Helpers
    // function checkNoPendingTrigger(
    //     address trader,
    //     uint pairIndex,
    //     uint index,
    //     StorageInterface.LimitOrder orderType
    // ) private view {
    //     NftRewardsInterfaceV6_3_1.TriggeredLimitId
    //         memory triggeredLimitId = NftRewardsInterfaceV6_3_1
    //             .TriggeredLimitId(trader, pairIndex, index, orderType);
    //     require(
    //         !nftRewards.triggered(triggeredLimitId) ||
    //             nftRewards.timedOut(triggeredLimitId),
    //         "PENDING_TRIGGER"
    //     );
    // }

    // function canExecute(
    //     StorageInterface.LimitOrder orderType,
    //     TradingCallbacksInterface.SimplifiedTradeId memory id
    // ) private view returns (bool) {
    //     if (orderType == StorageInterface.LimitOrder.LIQ) return true;

    //     uint b = ChainUtils.getBlockNumber();
    //     address cb = storageT.callbacks();

    //     if (orderType == StorageInterface.LimitOrder.TP)
    //         return !cb.isTpInTimeout(id, b);
    //     if (orderType == StorageInterface.LimitOrder.SL)
    //         return !cb.isSlInTimeout(id, b);

    //     return !cb.isLimitInTimeout(id, b);
    // }

    function pairMaxLeverage(
        PairsStorageInterfaceV6 pairsStored,
        uint pairIndex
    ) private view returns (uint) {
        uint max = TradingCallbacksInterface(address(storageT.callbacks()))
            .pairMaxLeverage(pairIndex);
        return max > 0 ? max : pairsStored.pairMaxLeverage(pairIndex);
    }

    // function handleBotInUse(
    //     address sender,
    //     uint nftId,
    //     address trader,
    //     uint pairIndex,
    //     uint index
    // ) private {
    //     (bytes32 nftHash, bytes32 botHash) = nftRewards.getNftBotHashes(
    //         block.number,
    //         sender,
    //         nftId,
    //         trader,
    //         pairIndex,
    //         index
    //     );
    //     require(!nftRewards.nftBotInUse(nftHash, botHash), "BOT_IN_USE");

    //     nftRewards.setNftBotInUse(nftHash, botHash);
    // }

    function getPriceNftOrder(
        bool isOpenLimit,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksInterface.TradeType tradeType,
        StorageInterface.LimitOrder orderType,
        uint leveragedPosWETH
    ) private returns (uint orderId, uint linkFee) {
        TradingCallbacksInterface.LastUpdated
            memory lastUpdated = TradingCallbacksInterface(
                address(storageT.callbacks())
            ).tradeLastUpdated(trader, pairIndex, index, tradeType);

        AggregatorInterfaceV1_4 aggregator = AggregatorInterfaceV1_4(
            address(storageT.priceAggregator())
        );

        orderId = aggregator.getPrice(
            pairIndex,
            isOpenLimit
                ? AggregatorInterfaceV1_4.OrderType.LIMIT_OPEN
                : AggregatorInterfaceV1_4.OrderType.LIMIT_CLOSE,
            leveragedPosWETH,
            isOpenLimit
                ? lastUpdated.limit
                : orderType == StorageInterface.LimitOrder.SL
                ? lastUpdated.sl
                : orderType == StorageInterface.LimitOrder.TP
                ? lastUpdated.tp
                : lastUpdated.created
        );

        // linkFee = aggregator.linkFee(pairIndex, leveragedPosWETH);
    }
}

contracts/TradingCallbacks.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/StorageInterface.sol";
import "./interfaces/NFTRewardInterfaceV6_3.sol";
import "./interfaces/PairInfosInterface.sol";
import "./interfaces/ReferralsInterface.sol";
import "./interfaces/StakingInterface.sol";
import "./libraries/ChainUtils.sol";
import "./interfaces/BorrowingFeesInterface.sol";
import "./interfaces/PairsStorageInterfaceV6.sol";
import "./interfaces/AggregatorInterfaceV1_4.sol";
import "./Storage.sol";

contract TradingCallbacks is Initializable {
    // Contracts (constant)
    StorageInterface public storageT;
    NftRewardsInterfaceV6_3_1 public nftRewards;
    PairInfosInterface public pairInfos;
    ReferralsInterface public referrals;
    StakingInterface public staking;

    // Params (constant)
    uint constant PRECISION = 1e10; // 10 decimals

    uint constant MAX_SL_P = 75; // -75% PNL
    uint constant MAX_GAIN_P = 900; // 900% PnL (10x)
    uint constant MAX_EXECUTE_TIMEOUT = 5; // 5 blocks

    // Params (adjustable)
    uint public WETHVaultFeeP; // % of closing fee going to WETH vault (eg. 40)
    uint public lpFeeP; // % of closing fee going to GNS/WETH LPs (eg. 20)
    uint public sssFeeP; // % of closing fee going to GNS staking (eg. 40)
    uint public vaultFeeP = 50;
    uint public liquidatorFeeP = 50;
    uint public liquidationFeeP = 5;
    uint public parLiquidationFeeP = 3;

    // State
    bool public isPaused; // Prevent opening new trades
    bool public isDone; // Prevent any interaction with the contract
    uint public canExecuteTimeout; // How long an update to TP/SL/Limit has to wait before it is executable

    // Last Updated State
    mapping(address => mapping(uint => mapping(uint => mapping(TradeType => LastUpdated))))
        public tradeLastUpdated; // Block numbers for last updated

    // v6.3.2 Storage/State
    BorrowingFeesInterface public borrowingFees;

    mapping(uint => uint) public pairMaxLeverage;

    // v6.4 Storage
    mapping(address => mapping(uint => mapping(uint => mapping(TradeType => TradeData))))
        public tradeData; // More storage for trades / limit orders

    // Custom data types
    struct AggregatorAnswer {
        uint orderId;
        uint price;
        uint spreadP;
        uint open;
        uint high;
        uint low;
    }

    struct PendingMarketOrder {
        Trade trade;
        uint block;
        uint wantedPrice; // PRECISION
        uint slippageP; // PRECISION (%)
        uint spreadReductionP;
        uint tokenId; // index in supportedTokens
    }

    struct Trade {
        address trader;
        uint pairIndex;
        uint index;
        uint initialPosToken; // 1e18
        uint positionSizeWETH; // 1e18
        uint openPrice; // PRECISION
        bool buy;
        uint leverage;
        uint tp; // PRECISION
        uint sl; // PRECISION
    }

    // Useful to avoid stack too deep errors
    struct Values {
        uint posWETH;
        uint levPosWETH;
        uint tokenPriceWETH;
        int profitP;
        uint price;
        uint liqPrice;
        uint WETHSentToTrader;
        uint reward1;
        uint reward2;
        uint reward3;
        bool exactExecution;
    }

    struct SimplifiedTradeId {
        address trader;
        uint pairIndex;
        uint index;
        TradeType tradeType;
    }

    struct LastUpdated {
        uint32 tp;
        uint32 sl;
        uint32 limit;
        uint32 created;
    }

    struct TradeData {
        uint40 maxSlippageP; // 1e10 (%)
        uint216 _placeholder; // for potential future data
    }

    struct OpenTradePrepInput {
        uint executionPrice;
        uint wantedPrice;
        uint marketPrice;
        uint spreadP;
        uint spreadReductionP;
        bool buy;
        uint pairIndex;
        uint positionSize;
        uint leverage;
        uint maxSlippageP;
        uint tp;
        uint sl;
    }

    struct feeConfig {
        uint _vaultFeeP;
        uint _liquidatorFeeP;
        uint _liquidationFeeP;
        uint _parLiquidationFeeP;
    }

    enum TradeType {
        MARKET,
        LIMIT
    }

    enum CancelReason {
        NONE,
        PAUSED,
        MARKET_CLOSED,
        SLIPPAGE,
        TP_REACHED,
        SL_REACHED,
        EXPOSURE_LIMITS,
        PRICE_IMPACT,
        MAX_LEVERAGE,
        NO_TRADE,
        WRONG_TRADE,
        NOT_HIT
    }

    // Events
    event MarketExecuted(
        uint indexed orderId,
        StorageInterface.Trade t,
        bool open,
        uint price,
        uint priceImpactP,
        uint positionSizeWETH,
        int percentProfit, // before fees
        uint WETHSentToTrader
    );

    event LimitExecuted(
        uint indexed orderId,
        uint limitIndex,
        StorageInterface.Trade t,
        address indexed nftHolder,
        StorageInterface.LimitOrder orderType,
        uint price,
        uint priceImpactP,
        uint positionSizeWETH,
        int percentProfit,
        uint WETHSentToTrader,
        bool exactExecution
    );

    event MarketOpenCanceled(
        uint indexed orderId,
        address indexed trader,
        uint indexed pairIndex,
        CancelReason cancelReason
    );
    event MarketCloseCanceled(
        uint indexed orderId,
        address indexed trader,
        uint indexed pairIndex,
        uint index,
        CancelReason cancelReason
    );
    event NftOrderCanceled(
        uint indexed orderId,
        address indexed nftHolder,
        StorageInterface.LimitOrder orderType,
        CancelReason cancelReason
    );

    event ClosingFeeSharesPUpdated(
        uint WETHVaultFeeP,
        uint lpFeeP,
        uint sssFeeP
    );
    event CanExecuteTimeoutUpdated(uint newValue);

    event Pause(bool paused);
    event Done(bool done);

    event DevGovFeeCharged(address indexed trader, uint valueWETH);
    event ReferralFeeCharged(address indexed trader, uint valueWETH);
    event NftBotFeeCharged(address indexed trader, uint valueWETH);
    event SssFeeCharged(address indexed trader, uint valueWETH);
    event WETHVaultFeeCharged(address indexed trader, uint valueWETH);
    event BorrowingFeeCharged(
        address indexed trader,
        uint tradeValueWETH,
        uint feeValueWETH
    );
    event PairMaxLeverageUpdated(uint indexed pairIndex, uint maxLeverage);

    // Custom errors (save gas)
    error WrongParams();
    error Forbidden();

    function initialize(
        StorageInterface _storageT,
        NftRewardsInterfaceV6_3_1 _nftRewards,
        PairInfosInterface _pairInfos,
        ReferralsInterface _referrals,
        StakingInterface _staking,
        BorrowingFeesInterface _borrowingFees,
        address vaultToApprove,
        uint _WETHVaultFeeP,
        uint _lpFeeP,
        uint _sssFeeP,
        uint _canExecuteTimeout,
        feeConfig memory data
    ) external initializer {
        if (
            address(_storageT) == address(0) ||
            address(_nftRewards) == address(0) ||
            address(_pairInfos) == address(0) ||
            address(_referrals) == address(0) ||
            address(_staking) == address(0) ||
            vaultToApprove == address(0) ||
            _WETHVaultFeeP + _lpFeeP + _sssFeeP != 100 ||
            _canExecuteTimeout > MAX_EXECUTE_TIMEOUT
        ) {
            revert WrongParams();
        }

        storageT = _storageT;
        nftRewards = _nftRewards;
        pairInfos = _pairInfos;
        referrals = _referrals;
        staking = _staking;
        borrowingFees = _borrowingFees;

        WETHVaultFeeP = _WETHVaultFeeP;
        lpFeeP = _lpFeeP;
        sssFeeP = _sssFeeP;
        vaultFeeP = data._vaultFeeP;
        liquidatorFeeP = data._liquidatorFeeP;
        liquidationFeeP = data._liquidationFeeP;
        parLiquidationFeeP = data._parLiquidationFeeP;

        canExecuteTimeout = _canExecuteTimeout;
        TokenInterface t = storageT.WETH();
        t.approve(address(staking), type(uint256).max);
        t.approve(vaultToApprove, type(uint256).max);
    }

    // function initializeV2(
    //     BorrowingFeesInterface _borrowingFees
    // ) external reinitializer(2) {
    //     if (address(_borrowingFees) == address(0)) {
    //         revert WrongParams();
    //     }
    //     borrowingFees = _borrowingFees;
    // }

    // Modifiers
    modifier onlyGov() {
        isGov();
        _;
    }
    modifier onlyPriceAggregator() {
        isPriceAggregator();
        _;
    }
    modifier notDone() {
        isNotDone();
        _;
    }
    modifier onlyTrading() {
        isTrading();
        _;
    }
    modifier onlyManager() {
        isManager();
        _;
    }

    // Saving code size by calling these functions inside modifiers
    function isGov() private view {
        if (msg.sender != storageT.gov()) {
            revert Forbidden();
        }
    }

    function isPriceAggregator() private view {
        if (msg.sender != address(storageT.priceAggregator())) {
            revert Forbidden();
        }
    }

    function isNotDone() private view {
        if (isDone) {
            revert Forbidden();
        }
    }

    function isTrading() private view {
        if (msg.sender != address(storageT.trading())) {
            revert Forbidden();
        }
    }

    function isManager() private view {
        if (msg.sender != pairInfos.manager()) {
            revert Forbidden();
        }
    }

    // Manage params
    function setPairMaxLeverage(uint pairIndex, uint maxLeverage) external {
        _setPairMaxLeverage(pairIndex, maxLeverage);
    }

    function setPairMaxLeverageArray(
        uint[] calldata indices,
        uint[] calldata values
    ) external onlyManager {
        uint len = indices.length;

        if (len != values.length) {
            revert WrongParams();
        }

        for (uint i; i < len; ) {
            _setPairMaxLeverage(indices[i], values[i]);
            unchecked {
                ++i;
            }
        }
    }

    function _setPairMaxLeverage(uint pairIndex, uint maxLeverage) private {
        pairMaxLeverage[pairIndex] = maxLeverage;
        emit PairMaxLeverageUpdated(pairIndex, maxLeverage);
    }

    function setClosingFeeSharesP(
        uint _WETHVaultFeeP,
        uint _lpFeeP,
        uint _sssFeeP
    ) external onlyGov {
        if (_WETHVaultFeeP + _lpFeeP + _sssFeeP != 100) {
            revert WrongParams();
        }

        WETHVaultFeeP = _WETHVaultFeeP;
        lpFeeP = _lpFeeP;
        sssFeeP = _sssFeeP;

        emit ClosingFeeSharesPUpdated(_WETHVaultFeeP, _lpFeeP, _sssFeeP);
    }

    function setCanExecuteTimeout(uint _canExecuteTimeout) external onlyGov {
        if (_canExecuteTimeout > MAX_EXECUTE_TIMEOUT) {
            revert WrongParams();
        }
        canExecuteTimeout = _canExecuteTimeout;
        emit CanExecuteTimeoutUpdated(_canExecuteTimeout);
    }

    // Manage state
    function pause() external onlyGov {
        isPaused = !isPaused;

        emit Pause(isPaused);
    }

    function done() external onlyGov {
        isDone = !isDone;

        emit Done(isDone);
    }

    // Callbacks
    function openTradeMarketCallback(
        AggregatorAnswer memory a,
        StorageInterface.PendingMarketOrder memory o
    ) external onlyPriceAggregator notDone {
        // StorageInterface.PendingMarketOrder memory o = getPendingMarketOrder(
        //     a.orderId
        // );

        // if (o.block == 0) {
        //     return;
        // }

        StorageInterface.Trade memory t = o.trade;

        (
            uint priceImpactP,
            uint priceAfterImpact,
            CancelReason cancelReason
        ) = _openTradePrep(
                OpenTradePrepInput(
                    a.price,
                    o.wantedPrice,
                    a.price,
                    a.spreadP,
                    o.spreadReductionP,
                    t.buy,
                    t.pairIndex,
                    t.positionSizeWETH,
                    t.leverage,
                    o.slippageP,
                    t.tp,
                    t.sl
                )
            );
        t.openPrice = priceAfterImpact;
        if (cancelReason == CancelReason.NONE) {
            (StorageInterface.Trade memory finalTrade, ) = registerTrade(
                t,
                1500,
                0
            );

            emit MarketExecuted(
                a.orderId,
                finalTrade,
                true,
                finalTrade.openPrice,
                priceImpactP,
                (finalTrade.positionSizeWETH * finalTrade.leverage),
                0,
                0
            );
        } else {
            // uint devGovFeesWETH = storageT.handleDevGovFees(
            //     t.pairIndex,
            //     t.positionSizeWETH * t.leverage,
            //     true,
            //     true
            // );
            // transferFromStorageToAddress(
            //     t.trader,
            //     t.positionSizeWETH - devGovFeesWETH
            // );

            // emit DevGovFeeCharged(t.trader, devGovFeesWETH);
            emit MarketOpenCanceled(
                a.orderId,
                t.trader,
                t.pairIndex,
                cancelReason
            );
        }

        // storageT.unregisterPendingMarketOrder(a.orderId, true);
    }

    function closeTradeMarketCallback(
        AggregatorAnswer memory a,
        StorageInterface.PendingMarketOrder memory o
    ) external onlyPriceAggregator notDone {
        // StorageInterface.PendingMarketOrder memory o = getPendingMarketOrder(
        //     a.orderId
        // );

        // if (o.block == 0) {
        //     return;
        // }

        StorageInterface.Trade memory t = getOpenTrade(
            o.trade.trader,
            o.trade.pairIndex,
            o.trade.index
        );

        CancelReason cancelReason = t.leverage == 0
            ? CancelReason.NO_TRADE
            : (a.price == 0 ? CancelReason.MARKET_CLOSED : CancelReason.NONE);

        if (cancelReason != CancelReason.NO_TRADE) {
            StorageInterface.TradeInfo memory i = getOpenTradeInfo(
                t.trader,
                t.pairIndex,
                t.index
            );
            AggregatorInterfaceV1_4 aggregator = AggregatorInterfaceV1_4(
                address(storageT.priceAggregator())
            );

            Values memory v;
            v.levPosWETH = (t.positionSizeWETH * t.leverage);
            // v.tokenPriceWETH = aggregator.tokenPriceWETH();

            if (cancelReason == CancelReason.NONE) {
                v.profitP = currentPercentProfit(
                    t.openPrice,
                    a.price,
                    t.buy,
                    t.leverage
                );
                v.posWETH = v.levPosWETH / t.leverage;

                v.WETHSentToTrader = unregisterTrade(
                    t,
                    true,
                    v.profitP,
                    v.posWETH,
                    i.openInterestWETH,
                    // (v.levPosWETH *
                    //     aggregator.pairsStorage().pairCloseFeeP(t.pairIndex)) /
                    //     100 /
                    //     PRECISION,
                    // (v.levPosWETH *
                    //     aggregator.pairsStorage().pairNftLimitOrderFeeP(
                    //         t.pairIndex
                    //     )) /
                    //     100 /
                    //     PRECISION
                    0,
                    0
                );

                emit MarketExecuted(
                    a.orderId,
                    t,
                    false,
                    a.price,
                    0,
                    v.posWETH,
                    v.profitP,
                    v.WETHSentToTrader
                );
            } else {
                // Dev / gov rewards to pay for oracle cost
                // Charge in WETH if collateral in storage or token if collateral in vault
                // v.reward1 = t.positionSizeWETH > 0
                //     ? storageT.handleDevGovFees(
                //         t.pairIndex,
                //         v.levPosWETH,
                //         true,
                //         true
                //     )
                //     : (storageT.handleDevGovFees(
                //         t.pairIndex,
                //         (v.levPosWETH * PRECISION) / v.tokenPriceWETH,
                //         false,
                //         true
                //     ) * v.tokenPriceWETH) / PRECISION;

                // t.initialPosToken -= (v.reward1 * PRECISION) / i.tokenPriceWETH;
                // storageT.updateTrade(t);

                emit DevGovFeeCharged(t.trader, v.reward1);
            }
        }

        if (cancelReason != CancelReason.NONE) {
            emit MarketCloseCanceled(
                a.orderId,
                o.trade.trader,
                o.trade.pairIndex,
                o.trade.index,
                cancelReason
            );
        }

        storageT.unregisterPendingMarketOrder(a.orderId, false);
    }

